package com.gleasondev.spring6restmvc.entities;import jakarta.persistence.*;import org.hibernate.annotations.GenericGenerator;import org.hibernate.annotations.JdbcTypeCode;import org.hibernate.type.SqlTypes;import java.time.LocalDateTime;import java.util.UUID;@Entitypublic class Customer {    @Id    @GeneratedValue(generator = "UUID")    @GenericGenerator(name = "UUID", strategy = "org.hibernate.id.UUIDGenerator")    @JdbcTypeCode(SqlTypes.CHAR)    @Column(length = 36, columnDefinition = "varchar(36)", updatable = false, nullable = false)    private UUID id;    private String name;    @Version    private Integer version;    private LocalDateTime createdDate;    private LocalDateTime updateDate;    @Column(length = 255)    private String email;    public Customer(UUID id, String name, Integer version, LocalDateTime createdDate, LocalDateTime updateDate, String email) {        this.id = id;        this.name = name;        this.version = version;        this.createdDate = createdDate;        this.updateDate = updateDate;        this.email = email;    }    public Customer() {    }    public static CustomerBuilder builder() {        return new CustomerBuilder();    }    public UUID getId() {        return this.id;    }    public void setId(UUID id) {        this.id = id;    }    public String getName() {        return this.name;    }    public void setName(String name) {        this.name = name;    }    public Integer getVersion() {        return this.version;    }    public void setVersion(Integer version) {        this.version = version;    }    public LocalDateTime getCreatedDate() {        return this.createdDate;    }    public void setCreatedDate(LocalDateTime createdDate) {        this.createdDate = createdDate;    }    public LocalDateTime getUpdateDate() {        return this.updateDate;    }    public void setUpdateDate(LocalDateTime updateDate) {        this.updateDate = updateDate;    }    public String getEmail() {        return this.email;    }    public void setEmail(String email) {        this.email = email;    }    public static class CustomerBuilder {        private UUID id;        private String name;        private Integer version;        private LocalDateTime createdDate;        private LocalDateTime updateDate;        private String email;        CustomerBuilder() {        }        public CustomerBuilder id(UUID id) {            this.id = id;            return this;        }        public CustomerBuilder name(String name) {            this.name = name;            return this;        }        public CustomerBuilder version(Integer version) {            this.version = version;            return this;        }        public CustomerBuilder createdDate(LocalDateTime createdDate) {            this.createdDate = createdDate;            return this;        }        public CustomerBuilder updateDate(LocalDateTime updateDate) {            this.updateDate = updateDate;            return this;        }        public CustomerBuilder email(String email) {            this.email = email;            return this;        }        public Customer build() {            return new Customer(id, name, version, createdDate, updateDate, email);        }        public String toString() {            return "Customer.CustomerBuilder(id=" + this.id + ", name=" + this.name + ", version=" + this.version + ", createdDate=" + this.createdDate + ", updateDate=" + this.updateDate + ", email=" + this.email + ")";        }    }}