package com.gleasondev.spring6restmvc.controller;import com.gleasondev.spring6restmvc.model.CustomerDTO;import com.gleasondev.spring6restmvc.repositories.CustomerRepository;import jakarta.transaction.Transactional;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.annotation.Rollback;import java.util.List;import java.util.UUID;import static org.assertj.core.api.Assertions.assertThat;import static org.junit.jupiter.api.Assertions.assertThrows;@SpringBootTestclass CustomerControllerIT {    @Autowired    CustomerController customerController;    @Autowired    CustomerRepository customerRepository;    @Test    void testListCustomers() {        List<CustomerDTO> dtos = customerController.listCustomers();        assertThat(dtos.size()).isEqualTo(3);    }    @Rollback    @Transactional    @Test    void testEmptyList() {        customerRepository.deleteAll();        List<CustomerDTO> dtos = customerController.listCustomers();        assertThat(dtos.size()).isEqualTo(0);    }    @Test    void testGetCustomerById() {        CustomerDTO dto = customerController.getCustomerById(customerRepository.findAll().get(0).getId());        assertThat(dto).isNotNull();    }    @Test    void testCustomerNotFound() {        assertThrows(NotFoundException.class,                () -> customerController.getCustomerById(UUID.randomUUID()));    }}