package com.gleasondev.spring6restmvc.repositories;import com.gleasondev.spring6restmvc.entities.Customer;import org.assertj.core.api.Assertions;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTest;@DataJpaTestclass CustomerRepositoryTest {    @Autowired    CustomerRepository customerRepository;    @Test    void testSaveBeer() {        Customer savedCustomer = customerRepository.save(Customer.builder()                                                                 .name("New Name")                                                                 .build());        Assertions.assertThat(savedCustomer).isNotNull();        Assertions.assertThat(savedCustomer.getId()).isNotNull();    }}